-- Table: CVDictionary
CREATE TABLE CVDictionary ( 
    id              INTEGER     PRIMARY KEY AUTOINCREMENT,
    CVTag           CHAR( 10 ),
    CVName          VARCHAR,
    SignNumber      INT,
    SignValue       VARCHAR,
    SignDescription VARCHAR 
);


-- Table: EMR
CREATE TABLE EMR ( 
    id              INTEGER    PRIMARY KEY AUTOINCREMENT,
    innerElementTag VARCHAR,
    dataElementTag  VARCHAR,
    dataElementName VARCHAR,
    repeatTimes     CHAR( 4 ),
    definition      VARCHAR,
    dataType        CHAR( 1 ),
    displayType     VARCHAR,
    allowRange      VARCHAR,
    constraintTag   CHAR( 1 ) 
);

